import React, { useState } from 'react';
import PropTypes from 'prop-types';
import moment from 'moment';
import styled from '@emotion/styled';
import WeatherBanner from '../WeatherBanner/WeatherBanner';
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    Brush,
    AreaChart,
    Area,
    ResponsiveContainer,
  } from 'recharts';
const WeatherBannerTab = ({
  location,
  forecastOfDay,
  locale,
  unit,
}) => {
  const buildData = forecastOfDay.map((item, index) => {
    return {
      ...item,
      time: item.moment.locale(locale).format('h:mm a')
    }
  });
  return (
    <Container>
      <LocationText>{location}</LocationText>
      <WeatherBanner forecastNow={forecastOfDay[0]} unit={unit} />
      <ResponsiveContainer width="100%" height={200}>
        <AreaChart
        width={1000}
        height={200}
        data={buildData}
        syncId="anyId"
        margin={{
            top: 10,
            right: 30,
            left: 0,
            bottom: 0,
        }}
        >
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="dt" />
        <YAxis dataKey='' />
        <Tooltip />
        <Area type="monotone" dataKey="temp" stroke="#82ca9d" fill="#82ca9d" />
        </AreaChart>
      </ResponsiveContainer>
    </Container>
  );
};

WeatherBannerTab.defaultProps = {
  unit: 'metric',
  locale: '',
  forecastOfDay: [],
};

WeatherBannerTab.propTypes = {
  location: PropTypes.string.isRequired,
  forecastOfDay: PropTypes.arrayOf(
    PropTypes.shape({
      dt: PropTypes.number.isRequired,
      temp: PropTypes.number.isRequired,
      temp_min: PropTypes.number.isRequired,
      temp_max: PropTypes.number.isRequired,
      humidity: PropTypes.number.isRequired,
      icon: PropTypes.string.isRequired,
      desc: PropTypes.string.isRequired,
      clouds: PropTypes.number.isRequired,
      wind: PropTypes.number.isRequired,
    }),
  ),
  locale: PropTypes.string,
  unit: PropTypes.string,
};

export default WeatherBannerTab;

const LocationText = styled.div`
  font-size: 2rem;
`;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-around;
`;

const TabContainer = styled.div`
  margin: 0.8rem 0.8rem;
  padding-bottom: 1.5rem;
`;
