import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import { useCurrentLocation } from './hooks/useCurrentLocation';
import WeatherWidget from './components/WeatherWidget/WeatherWidget';
// mock data for testing without API call
import testData from './testData.json';

const OPEN_WEATHER_MAP_KEY = '524589f12ff60e2a150e470595208862';
const OPEN_CAGE_DATA_KEY = '0264db5477fe4b1c9ee9ae23b3ec59e5';
const cities = [
  { city: '', label: '' },
  { city: 'taipei', label: 'ðŸ‡¹ðŸ‡¼ Taipei' },
  { city: 'tokyo', label: 'ðŸ‡¯ðŸ‡µ Tokyo' },
  { city: 'moscow', label: 'ðŸ‡·ðŸ‡º Moscow' },
  { city: 'sydney', label: 'ðŸ‡¦ðŸ‡º Sydney' },
  { city: 'london', label: 'ðŸ‡¬ðŸ‡§ London' },
  { city: 'paris', label: 'ðŸ‡«ðŸ‡· Paris' },
  { city: 'mexico', label: 'ðŸ‡²ðŸ‡½ Mexico' },
  { city: 'seattle', label: 'ðŸ‡ºðŸ‡¸ Seattle' },
  { city: 'washington', label: 'ðŸ‡ºðŸ‡¸ Washington' },
  { city: 'beijing', label: 'ðŸ‡¨ðŸ‡³ Beijing' },
];
const geolocationOptions = {
  // Using this option you can define when should the location request timeout and
  // call the error callback with timeout message.
  timeout: 1000 * 60 * 1 // 1 min (1000 ms * 60 sec * 1 minute = 60 000ms)
};

const App = () => {
  const params = new URLSearchParams(window.location.search);
  const city = params.get('city_index');
  const [cityIndex, setCityIndex] = useState(city || 0); 
  const [forecast, setForecast] = useState([]);
  const [error, setError] = useState('');
  const [currentCityInfo, setCurrentCityInfo] = useState({});
  // store location in state
  const [location, setLocation] = useState({});
  // store error message in state

  const oldCoords = localStorage.getItem('coords');

  // Success handler for geolocation's `getCurrentPosition` method
  const handleSuccess = position => {
    const { latitude, longitude } = position.coords;
    console.log('in success', position);
    localStorage.setItem('coords', position.coords);
    setLocation({
      latitude,
      longitude
    });
  };

  // Error handler for geolocation's `getCurrentPosition` method
  const handleError = error => {
    setError(error.message);
  };
  // ...

  if(!navigator.geolocation) {
    console.log('Geolocation is not supported by your browser');
  } else {
    // console.log('Locatingâ€¦');
    navigator.geolocation.getCurrentPosition(handleSuccess, handleError);
  }

  const getCityName = async (latitude, longitude) => {
    try {
      if(latitude && longitude) {
        // const response = await axios.get( `https://api.opencagedata.com/geocode/v1/json?q=${latitude}+${longitude}&key=${OPEN_CAGE_DATA_KEY}`);
        if(response.data.results[0]) {
          setCurrentCityInfo({city: response.data.results[0].components.village, label: response.data.results[0].annotations.flag});
        }
      } else {

      }
    } catch (err) {
      if (OPEN_CAGE_DATA_KEY.length === 0) {
        setError('');
      } else {
        setError(err.stack);
      }
    }
  }

  const fetchWeatherAsync = async (latitude, longitude, cityId) => {
    let queryParams = {}
    if(latitude&&longitude) {
      queryParams = {
        lat: latitude,
        lon: longitude,
        lang: 'en_us',
        appid: OPEN_WEATHER_MAP_KEY,
        units: 'metric',
      }
    } else {
      queryParams = {
        q: cityId,
        lang: 'en_us',
        appid: OPEN_WEATHER_MAP_KEY,
        units: 'metric',
      };
    }
    try {
      const response = await axios.get(
        'https://api.openweathermap.org/data/2.5/forecast',
        {
          params: queryParams,
        },
      );
      const transformData = await response.data.list.map((data) => ({
        dt: data.dt,
        temp: data.main.temp,
        temp_min: data.main.temp_min,
        temp_max: data.main.temp_max,
        humidity: data.main.humidity,
        icon: data.weather[0].icon,
        desc: data.weather[0].description,
        clouds: data.clouds.all,
        wind: data.wind.speed,
      }));
      setForecast(transformData);
    } catch (err) {
      if (OPEN_WEATHER_MAP_KEY.length === 0) {
        // Use mock data if no key
        const transformData = await testData.list.map((data) => ({
          dt: data.dt,
          temp: data.main.temp,
          temp_min: data.main.temp_min,
          temp_max: data.main.temp_max,
          humidity: data.main.humidity,
          icon: data.weather[0].icon,
          desc: data.weather[0].description,
          clouds: data.clouds.all,
          wind: data.wind.speed,
        }));
        setForecast(transformData);
        setError('');
      } else {
        setError(err.stack);
      }
    }
  };

  useEffect(() => {
    fetchWeatherAsync(location.latitude, location.longitude);
  }, [location]); // notice the empty array here

  useEffect(() => {
    getCityName(location.latitude, location.longitude);
  }, [location]);

  return (
    <div className="App">
      {error.length === 0 ? (
        <WeatherWidget
          config={{
            location: cities[cityIndex].label,
            unit: 'metric',
            locale: 'zh-tw',
            onLocationClick: () => {
              if (cityIndex + 1 >= cities.length) {
                setCityIndex(0);
                fetchWeatherAsync(cities[0].city);
              } else {
                setCityIndex(cityIndex + 1);
                fetchWeatherAsync(cities[cityIndex + 1].city);
              }
            },
          }}
          forecast={forecast}
        />
      ) : (
        <div>
          <h2>Unable to fetch weather data!</h2>
          <pre>{error}</pre>
        </div>
      )}
    </div>
  );
};

export default App;
