import { useState } from 'react';
const OPEN_CAGE_DATA_KEY = '0264db5477fe4b1c9ee9ae23b3ec59e5';

export const useCurrentLocation = (options,callback) => {
  // store location in state
  const [location, setLocation] = useState();
  // store error message in state
  const [error, setError] = useState();
  const [cityInfo, setCityInfo] = useState({});

  const getCityName = async ({ latitude, longitude }) => {
    try {
      const response = await axios.get( `https://api.opencagedata.com/geocode/v1/json?q=${latitude}+${longitude}&key=${OPEN_CAGE_DATA_KEY}`);
      console.log(response.data.results[0]);
      if(response.data.results[0]) {
        setCityInfo({city: response.data.results[0].components.village, label: response.data.results[0].annotations.flag});
      }
    } catch (err) {
      if (OPEN_WEATHER_MAP_KEY.length === 0) {
        // Use mock data if no key
        // const transformData = await testData.list.map((data) => ({
        //   dt: data.dt,
        //   temp: data.main.temp,
        //   temp_min: data.main.temp_min,
        //   temp_max: data.main.temp_max,
        //   humidity: data.main.humidity,
        //   icon: data.weather[0].icon,
        //   desc: data.weather[0].description,
        //   clouds: data.clouds.all,
        //   wind: data.wind.speed,
        // }));
        setError('');
      } else {
        setError(err.stack);
      }
    }
  }
  // Success handler for geolocation's `getCurrentPosition` method
  const handleSuccess = position => {
    const { latitude, longitude } = position.coords;

    setLocation({
      latitude,
      longitude
    });
    console.log(position.coords);
    getCityName({
      latitude,
      longitude
    })
  };

  // Error handler for geolocation's `getCurrentPosition` method
  const handleError = error => {
    setError(error.message);
  };
  // ...

  if(!navigator.geolocation) {
        console.log('Geolocation is not supported by your browser');
    } else {
        console.log('Locating…');
        navigator.geolocation.getCurrentPosition(handleSuccess, handleError, options);
    }    
  // Expose location result and the possible error message
  return { location, error, cityInfo };
}
